mapper.select:
select * from T_ETC_PARTNER_AUDIT partner_audit


        AND PARTNER_NAME like '%' || #{query.partnerName} || '%'


        AND SALER like '%' || #{query.saler} || '%'


        AND CREATE_TIME >= #{query.createStartTime} AND CREATE_TIME <= #{query.createEndTime}


        AND MODIFY_TIME >= #{query.modifyStartTime} AND CREATE_TIME <= #{query.modifyEndTime}

    AND STATUS = 4

select * from (select * from T_ETC_PARTNER_AUDIT partner_audit


        AND PARTNER_ID like '%' || #{query.partnerId} || '%'


        AND PARTNER_NAME like '%' || #{query.partnerName} || '%'


        AND PARTNER_TYPE like '%' || #{query.partnertType} || '%'


        AND PARENT_ID like '%' || #{query.parentId} || '%'


        AND CREATE_TIME >= #{query.createStartTime} AND CREATE_TIME <= #{query.createEndTime}


        AND MODIFY_TIME >= #{query.modifyStartTime} AND CREATE_TIME <= #{query.modifyEndTime}

    AND STATUS != 4

) where partner_id
not in (select partner_id from T_ETC_PARTNER_INFO where status in (2,3))
union
select * from T_ETC_PARTNER_STORAGE;
